customColumn1.cellBackground:
{{(() => { const tableData = tblProduction.processedTableData || []; return tableData.length > 0 ? tableData.map((currentRow, currentIndex) => (
(() => {
  const rows = tblProduction.tableData || [];

  const maxValue = Math.max(
    ...rows.map(r => Number(r["Total producir"] ?? 0)),
    1
  );

  const value = Number(currentRow["Total producir"] ?? 0);
  const pct = Math.round((value / maxValue) * 100);

  if (pct >= 95) return "#f3e8ff";   // morado claro
  if (pct >= 75) return "#eff6ff";   // azul claro
  if (pct >= 50) return "#ecfdf5";   // verde claro
  if (pct >= 30) return "#fffbeb";   // amarillo claro
  return "transparent";
})()
)) : 
